CREATE TABLE IF NOT EXISTS `plan_cuota` ( `id_plan_cuota` INTEGER  PRIMARY KEY AUTOINCREMENT NOT NULL, `id_factura` INTEGER NOT NULL, `cantidad_cuotas` INTEGER NOT NULL DEFAULT 2, `tipo_periodo` INTEGER NOT NULL DEFAULT 0, `fecha_inicio` DATETIME NOT NULL, `entrega_inicial` DOUBLE NOT NULL, `recargo` DOUBLE NOT NULL, FOREIGN KEY (`id_factura`) REFERENCES `factura`(`id_factura`) );