CREATE TABLE IF NOT EXISTS `caja` ( `id_caja` INT NOT NULL AUTO_INCREMENT PRIMARY KEY,  `nombre` TEXT NOT NULL ,`fecha_alta` DATE NOT NULL ,`saldo` DOUBLE NOT NULL ) ENGINE = InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;
CREATE TABLE IF NOT EXISTS `movimiento_caja` ( `id_movimiento` int( 11 ) NOT NULL AUTO_INCREMENT, `id_caja` int( 11 ) NOT NULL REFERENCES caja( id_caja ),`fecha_hora` datetime NOT NULL, `ingreso` double NOT NULL, `egreso` double NOT NULL, `razon` text NOT NULL, `responsable` text NOT NULL, `cierre` tinyint( 1 ) NOT NULL, PRIMARY KEY ( `id_movimiento` ) ) ENGINE = InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;