CREATE TABLE `compras` ( `id` bigint NOT NULL auto_increment, `id_proveedor` bigint NOT NULL, `fecha` date NOT NULL, `total` decimal(4,0) NOT NULL, `id_caja` INTEGER NULL REFERENCES `movimiento_caja`(`id_op_caja`), PRIMARY KEY  (`id`) ) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;
CREATE TABLE `compras_productos` ( `id` bigint NOT NULL auto_increment, `id_compra` bigint NOT NULL, `id_producto` bigint NOT NULL, `precio_compra` decimal(4,0) NOT NULL, `cantidad` decimal(4,0) NOT NULL, PRIMARY KEY  (`id`) ) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;