CREATE TABLE IF NOT EXISTS `recibos` ( `id_recibo` bigint(20) NOT NULL AUTO_INCREMENT, `id_cliente` bigint(20) REFERENCES clientes(id), `fecha_pago` datetime NOT NULL, `texto` tinyblob, `precio` double(15,4) DEFAULT NULL, `forma_pago` int(11) NOT NULL, `cancelado` tinyint(4) NOT NULL DEFAULT '0', `pagado` tinyint(4) NOT NULL DEFAULT '1', `serie` int(11) NOT NULL, `numero` int(11) NOT NULL, `id_caja` bigint(20) DEFAULT NULL, PRIMARY KEY (`id_recibo`), UNIQUE KEY `numero_recibo` (`serie`,`numero`) ) ENGINE=InnoDB;