CREATE TABLE IF NOT EXISTS `autos` ( `patente` varchar(10) NOT NULL, `id_dueno` int(4) REFERENCES `clientes`(`id`), `marca` text NOT NULL, `modelo` text, `ano` year(4) default NULL, `color` tinytext, `modificado` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP,  `creado` timestamp NOT NULL,  PRIMARY KEY  (`patente`(1))) ENGINE=MyISAM DEFAULT CHARSET=latin1;