CREATE TABLE IF NOT EXISTS `categoria_producto` ( `id` bigint(20) NOT NULL auto_increment,`nombre` text NOT NULL, `descripcion` text default NULL, PRIMARY KEY  (`id`) ) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;
CREATE TABLE IF NOT EXISTS `producto` ( `id` bigint(20) NOT NULL AUTO_INCREMENT, `id_categoria` bigint(20) NULL,  `codigo` text NOT NULL, `nombre` tinytext NOT NULL, `precio_costo` decimal(20,3) NULL, `precio_venta` decimal(20,3) NOT NULL, `descripcion` tinytext, `marca` tinytext, `stock` decimal(20,3) DEFAULT NULL, `habilitado` boolean NOT NULL DEFAULT 1, `modelo` tinytext NULL, FOREIGN KEY (id_categoria) REFERENCES categoria_producto(id), PRIMARY KEY (`id`)) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;