CREATE TABLE IF NOT EXISTS `categoria_producto` ( `id` int(10) NOT NULL auto_increment, `nombre` varchar(100) NOT NULL, `descripcion` varchar(200) default NULL, `tipo` int(1) NOT NULL, PRIMARY KEY  (`id`) ) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `producto` ( `id` bigint(1) NOT NULL AUTO_INCREMENT, `id_categoria` int(1) NOT NULL REFERENCES categoria_producto(id), `nombre` tinytext NOT NULL, `precio_costo` double(10,4) NOT NULL, `descripcion` tinytext, `marca` tinytext, `stock` double(10,3) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=InnoDB;