CREATE TABLE IF NOT EXISTS `categoria_producto` ( `id` bigint NOT NULL auto_increment, `nombre` varchar(100) NOT NULL, `descripcion` varchar(200) default NULL, `tipo` int NOT NULL, PRIMARY KEY  (`id`) ) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;
CREATE TABLE IF NOT EXISTS `producto` ( `id` bigint NOT NULL AUTO_INCREMENT, `id_categoria` bigint NOT NULL, `nombre` tinytext NOT NULL, `precio_costo` double NOT NULL, `precio_venta` double NOT NULL, `descripcion` tinytext, `marca` tinytext, `stock` double DEFAULT NULL, PRIMARY KEY (`id`)) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_spanish_ci;