CREATE TABLE `compras` ( `id` int(1) NOT NULL auto_increment, `id_proveedor` int(1) NOT NULL, `fecha` date NOT NULL, `costo` decimal(4,0) NOT NULL, PRIMARY KEY  (`id`) ) ENGINE=MyISAM AUTO_INCREMENT=86 DEFAULT CHARSET=latin1;
CREATE TABLE `compras_productos` ( `id` int(1) NOT NULL auto_increment, `id_compra` int(1) NOT NULL, `id_producto` int(1) NOT NULL, `precio_compra` decimal(4,0) NOT NULL, `cantidad` decimal(4,0) NOT NULL, PRIMARY KEY  (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
