CREATE TABLE IF NOT EXISTS `categoria` ( `id` int(10) NOT NULL auto_increment, `nombre` varchar(100) NOT NULL, `descripcion` varchar(200) default NULL, `tipo` int(10) NOT NULL default '-1', PRIMARY KEY  (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `producto` ( `id` int(1) NOT NULL auto_increment, `id_categoria` int(1) NOT NULL REFERENCES `categoria`(`id`), `nombre` tinytext NOT NULL, `precio_venta` decimal(4,0) NOT NULL, `descripcion` tinytext, `marca` tinytext, `precio_compra` decimal(4,0) NOT NULL default '0', PRIMARY KEY  (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `proveedor` ( `id` int(1) NOT NULL auto_increment, `nombre` tinytext NOT NULL, `direccion` blob,  `email` tinytext, `fax` tinytext, `telefono_celular` tinytext, `telefono_linea` tinytext, `cuit_cuil` tinytext, PRIMARY KEY  (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `compras` ( `id` int(1) NOT NULL auto_increment, `id_proveedor` int(1) NOT NULL REFERENCES `proveedor`(`id`), `fecha` date NOT NULL, PRIMARY KEY  (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `compras_productos`( `id` int(1) NOT NULL auto_increment, `id_compra` int(1) NOT NULL REFERENCES compras(id), `id_producto` int(1) NOT NULL REFERENCES producto(id), `precio_compra` decimal(4,0) NOT NULL, `cantidad` decimal(4,0) NOT NULL, PRIMARY KEY  (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `dueno` ( `id` int(1) NOT NULL auto_increment, `nombre` tinytext NOT NULL, `apellido` tinytext NOT NULL, `direccion` tinyblob, `email` tinytext, `telefono_celular` tinytext, `telefono_linea` tinytext, PRIMARY KEY  (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `mascota` ( `id` int(1) NOT NULL auto_increment, `id_dueno` int(1) NOT NULL REFERENCES dueno(id),   `nombre` tinytext NOT NULL, `raza` tinytext NOT NULL, `genero` tinytext NOT NULL, PRIMARY KEY  (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `peluqueria` ( `id` int(1) NOT NULL auto_increment, `id_mascota` mediumint(1) NOT NULL REFERENCES mascota(id), `descripcion` tinytext default NULL, `precio` decimal(4,0) NOT NULL, `fecha` date NOT NULL, PRIMARY KEY  (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `ventas` ( `id` int(1) NOT NULL auto_increment, `fecha` date NOT NULL,   PRIMARY KEY  (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `ventas_productos` ( `id` int(1) NOT NULL auto_increment, `id_venta` int(1) NOT NULL REFERENCES ventas(id),   `id_producto` int(1) NOT NULL REFERENCES producto(id), `precio` decimal(4,0) NOT NULL, `cantidad` decimal(4,0) NOT NULL, PRIMARY KEY  (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `gastos` ( `id` int(1) NOT NULL auto_increment, `id_categoria` int(1) NOT NULL REFERENCES categoria(id), `descripcion` int(1) NOT NULL, `costo` decimal(4,0) NOT NULL, `fecha` date NOT NULL, PRIMARY KEY  (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1;