CREATE TABLE IF NOT EXISTS `autos` ( `patente` varchar(10) NOT NULL, `id_dueno` int(4) REFERENCES `clientes`(`id`), `marca` text NOT NULL, `modelo` text, `ano` year(4) default NULL, `color` tinytext, `modificado` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP,  `creado` timestamp NOT NULL,  PRIMARY KEY  (`patente`(1))) ENGINE=MyISAM DEFAULT CHARSET=latin1;
CREATE TABLE IF NOT EXISTS `presupuesto` ( `id_presupuesto` int(1) NOT NULL auto_increment, `fecha` datetime NOT NULL, `kilometraje` int(1) NOT NULL, `id_auto` tinytext REFERENCES `autos`(`patente`), `titulo` tinytext, `contenido` longblob, `creado` datetime NOT NULL, `modificado` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP, `imprimir` tinyint(1), `email` tinyint(1), PRIMARY KEY  (`id_presupuesto`) ) ENGINE=MyISAM DEFAULT CHARSET=latin1