CREATE TABLE IF NOT EXISTS `servicios` ( `id_servicio` bigint(1) NOT NULL auto_increment, `nombre` tinytext NOT NULL, `fecha_alta` date NOT NULL,  `fecha_baja` date NULL,  `precio_base` double(10,3) NOT NULL,  `periodo` int(10) NOT NULL,  `dia_cobro` int(10) NOT NULL,  `forma_incompleto` int(10) NOT NULL,  PRIMARY KEY  (`id_servicio`) ) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `servicios_clientes` ( `id_servicio` bigint(1) REFERENCES servicios(id_servicio), `id_cliente` bigint(1) REFERENCES clientes(id), `fecha_alta` DATETIME NOT NULL, `fecha_baja` DATETIME NULL, PRIMARY KEY  (`id_servicio`, `id_cliente` )) ENGINE=InnoDB;
CREATE TABLE IF NOT EXISTS `recargos` ( `id_recargo` bigint(1) NOT NULL AUTO_INCREMENT, `id_servicio` bigint(1) NOT NULL,  `cant_dias` int(11) NOT NULL,  `porcentaje` double(2,2) DEFAULT NULL,  `recargo` double(10,3) DEFAULT NULL,  PRIMARY KEY (`id_recargo`) ) ENGINE=InnoDB;