CREATE TABLE `clientes` ( `id` int(1) NOT NULL auto_increment, `razon_social` tinytext NOT NULL, `nombre` tinytext, `segundo` tinytext, `apellido` tinytext, `calle` tinytext, `numero` int(2) default NULL, `piso` int(2) default NULL, `depto` tinytext, `ciudad` tinytext, `codigo_postal` tinytext, `provincia` tinytext, `pais` tinytext, `tel_fijo` tinytext, `tel_celular` tinytext, `fax` tinytext, `email` tinytext, `comprobante_email` tinyint(2) default 1, PRIMARY KEY  (`id`) );